mapper.select:
select count(*) from tbl_tran t join tbl_user u on t.owner=u.id join tbl_customer cus on t.customer_Id=cus.id left join tbl_contacts con on t.contacts_Id=con.id left join tbl_activity a on t.activity_Id=a.id
t.name like "%"#{name}"%"
and u.name like "%"#{owner}"%"
and cus.name like "%"#{customerId}"%"
and con.name like "%"#{contactsId}"%"
and t.stage =#{stage}
and t.type =#{type}
and t.source =#{source}
order by t.create_Time desc

select t.id, u.name as owner, t.money, t.name, t.expected_Date, cus.name as customer_Id, t.stage, t.type, t.source, a.name as activity_Id,con.full_name as contacts_Id, t.create_By, t.create_Time, t.edit_By, t.edit_Time, t.description, t.contact_Summary,t.next_Contact_Time from tbl_tran t join tbl_user u on t.owner=u.id join tbl_customer cus on t.customer_Id = cus.id left join tbl_contacts con on t.contacts_Id=con.id left join tbl_activity a on t.activity_Id=a.id
t.name like "%"#{name}"%"
and u.name like "%"#{owner}"%"
and cus.name like "%"#{customerId}"%"
and con.name like "%"#{contactsId}"%"
and t.stage =#{stage}
and t.type =#{type}
and t.source =#{source}
order by t.create_Time desc
